        }
        $Result | % {
            $CurrentHost = $_.dnshostname
            $CurrentPassword = $_.'ms-MCS-AdmPwd'
            if ([string]$CurrentPassword)
            {
                $TableLAPS.Rows.Add($CurrentHost,$CurrentPassword) | Out-Null
            }
        }
        $TableLAPS
    }
    End
    {               
    }
}
